for(var sliderButton=document.querySelectorAll(".slider__button"),i=0;i<sliderButton.length;i++)sliderButton[i].addEventListener("click",function(e){var t=e.target.parentElement.parentElement.querySelectorAll(".slider__item");if(e.target.classList.contains("slider__prev"))for(var i=0;i<=t.length-1;i++){if(t[i].classList.contains("slider__item--active")&&0!==i){t[i].classList.remove("slider__item--active"),t[i-1].classList.add("slider__item--active");break}if(t[i].classList.contains("slider__item--active")&&0==i){t[i].classList.remove("slider__item--active"),t[t.length-1].classList.add("slider__item--active");break}}else if(e.target.classList.contains("slider__next"))for(i=t.length-1;i>=0;i--){if(t[i].classList.contains("slider__item--active")&&i!==t.length-1){t[i].classList.remove("slider__item--active"),t[i+1].classList.add("slider__item--active");break}if(t[i].classList.contains("slider__item--active")&&i==t.length-1){t[i].classList.remove("slider__item--active"),t[0].classList.add("slider__item--active");break}}});for(var imageButtons=document.querySelectorAll(".slider__item--image"),videoButtons=document.querySelectorAll(".slider__item--video"),closeButtons=document.querySelectorAll(".slider__modal-close"),l=0;l<imageButtons.length;l++)imageButtons[l].addEventListener("click",function(e){e.preventDefault(),e.target.querySelector(".slider__modal").classList.add("slider__modal--active")});for(var m=0;m<videoButtons.length;m++)videoButtons[m].addEventListener("click",function(e){e.preventDefault(),e.target.querySelector(".slider__modal").classList.add("slider__modal--active")});for(var n=0;n<closeButtons.length;n++)closeButtons[n].addEventListener("click",function(e){e.preventDefault(),e.target.parentElement.parentElement.classList.remove("slider__modal--active")});